@import './common';

header {
    margin-top: 7.5%;
    margin-bottom: 2rem;

    .container {
        display: flex;
        justify-content: space-between;
        align-items: center;
        height: 60px;

        #top-navbar {
            width: 30%;

            ul {
                display: flex;
                justify-content: space-between;
                align-items: center;

                li {
                    cursor: pointer;
                    font-weight: bold;
                    margin-right: 0.5rem;
                }

                .menu-icon {
                    display: flex;
                    flex-direction: column;
                    height: 100%;
                    width: 60px;
                    min-width: 60px;
                    padding: 18px;
                    background-color: $black-color;

                    .line {
                        width: 100%;
                        height: 0.25rem;
                        background-color: $white-color;
                        margin-bottom: 4px;
                        border-radius: 1px;

                        &-half {
                            width: 50%;
                            height: 0.25rem;
                            background-color: $white-color;
                            margin-bottom: 4px;
                            border-radius: 1px;
                        }
                    }
                }
            }
        }

        .search-part {
            display: flex;
            justify-content: flex-end;
            align-items: center;
            width: 70%;

            &>span {
                margin-left: 2rem;

                .fa-search {
                    margin-right: 0.5rem;
                }

                input {
                    border: none;
                    border-bottom: 1px solid $black-color;
                    padding: 0.5rem 0;
                    font-weight: bold;
                    outline: none;

                    &:hover,
                    &:active,
                    &:focus {
                        border-bottom: 1px solid $primary-color;
                    }
                }
            }


        }
    }

    @media screen and (max-width: 768px) {
        margin-top: 5%;
    }
}

main {
    .container {
        display: grid;
        grid-template-columns: 1fr 3fr;
        grid-gap: 2rem;

        /* FILTERS STYLES */
        .filters {
            .title {
                @extend .grey-text;
                margin-bottom: 1.5rem;
            }

            .filter-option {
                display: flex;
                justify-content: space-between;
                align-items: center;
                padding: 0.75rem 0;
                margin-bottom: 0.5rem;
                border-bottom: 1.25px solid $grey-color;
                cursor: pointer;

                &-price {
                    display: flex;
                    flex-direction: column;
                    padding: 0.75rem 0;

                    .label {
                        margin-bottom: 1.5rem;
                    }

                    .slider {
                        -webkit-appearance: none;
                        outline: none;
                        background-color: $grey-color;
                        height: 1.5px;
                        margin-bottom: 1.5rem;

                        &::-webkit-slider-thumb {
                            -webkit-appearance: none;
                            background-color: $white-color;
                            width: 15px;
                            height: 15px;
                            border: 2px solid black;
                            border-radius: 50%;
                            cursor: pointer;
                        }
                    }

                    .range-value {
                        display: flex;
                        justify-content: space-between;
                        align-items: center;
                    }
                }
            }
        }

        /* FURNITURES GRID STYLES */
        .furnitures-grid {
            display: grid;
            grid-template-columns: repeat(3, 1fr);
            grid-gap: 2rem;

            .furniture-tile {
                .image-container {
                    position: relative;
                    cursor: pointer;

                    img {
                        width: 100%;
                        height: auto;
                        min-height: 150px;
                    }
                }

                .stripe {
                    position: absolute;
                    width: 65%;
                    height: 2rem;
                    background-color: $white-color;
                    bottom: 6px;
                }

                .type-price {
                    display: flex;
                    justify-content: space-between;
                    align-items: center;
                    margin-bottom: 0.5rem;

                    .type {
                        color: $grey-color;
                        font-size: 12px;
                    }
                }

                .ratings-cart {
                    display: flex;
                    justify-content: space-between;
                    align-items: center;

                    .ratings {
                        font-size: 12px;
                    }
                }
            }
        }
    }
}
