/* VARIABLES */
$primary-color: #e1a50f;
$grey-color: #a1a1a1;
$white-color: #ffffff;
$black-color: #000000;
$button-color: #070027;

/* PLACEHOLDER STYLES */
%flex-row {
    display: flex;
    justify-content: space-between;
    align-items: center;
}

%flex-column {
    display: flex;
    flex-direction: column;
}

/* GLOBAL STYLES */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    min-width: 320px;
    font-family: "Rubik", sans-serif;
    font-size: 14px;
    line-height: 1.5;
}

.container {
    width: 85%;
    max-width: 1280px;
    margin: auto;
    overflow: hidden;

    @media screen and (max-width: 768px) {
        width: 90%;
    }
}

ul {
    list-style: none;
}

a {
    text-decoration: none;
    color: inherit;
}

img {
    display: inline-block;
}

.grey-text {
    color: $grey-color;
}

.text-center {
    align-items: center;
}

.cart {
    font-size: 20px;
    cursor: pointer;
}

/* LOADER STYLES */
.loader,
.loader:after {
    border-radius: 50%;
    width: 6rem;
    height: 6rem;
}

.loader {
    margin: 60px auto;
    font-size: 10px;
    position: relative;
    text-indent: -9999em;
    border-top: 1.1em solid rgba(0,0,0, 0.2);
    border-right: 1.1em solid rgba(0,0,0, 0.2);
    border-bottom: 1.1em solid rgba(0,0,0, 0.2);
    border-left: 1.1em solid #000000;
    -webkit-transform: translateZ(0);
    -ms-transform: translateZ(0);
    transform: translateZ(0);
    -webkit-animation: load8 1.1s infinite linear;
    animation: load8 1.1s infinite linear;
}

@-webkit-keyframes load8 {
    0% {
        -webkit-transform: rotate(0deg);
        transform: rotate(0deg);
    }
    100% {
        -webkit-transform: rotate(360deg);
        transform: rotate(360deg);
    }
}

@keyframes load8 {
    0% {
        -webkit-transform: rotate(0deg);
        transform: rotate(0deg);
    }
    100% {
        -webkit-transform: rotate(360deg);
        transform: rotate(360deg);
    }
}

