header {
    margin-top: 5%;
    margin-bottom: 2rem;

    .container {
        @extend %flex-row;
        height: 60px;

        @media screen and (max-width: 768px) {
            display: flex;
            flex-direction: column;
            align-items: center;
            height: 120px;
        }

        #top-navbar {
            width: 30%;

            @media screen and (max-width: 768px) {
                width: 80%;
            }

            ul {
                @extend %flex-row;

                li {
                    cursor: pointer;
                    font-weight: bold;
                    margin-right: 0.5rem;

                    &:last-child {
                        margin-right: 0;
                    }
                }

                .menu-icon {
                    @extend %flex-column;
                    height: 100%;
                    width: 60px;
                    min-width: 60px;
                    padding: 18px;
                    background-color: $black-color;

                    .line {
                        width: 100%;
                        height: 0.25rem;
                        background-color: $white-color;
                        margin-bottom: 4px;
                        border-radius: 1px;

                        &-half {
                            width: 50%;
                            height: 0.25rem;
                            background-color: $white-color;
                            margin-bottom: 4px;
                            border-radius: 1px;
                        }
                    }
                }
            }
        }

        .search-part {
            display: flex;
            justify-content: flex-end;
            align-items: center;
            width: 70%;

            @media screen and (max-width: 768px) {
                width: 80%;
                justify-content: space-between;
            }

            &>span {
                margin-left: 2rem;
                cursor: pointer;

                @media screen and (max-width: 768px) {
                    margin-left: 0;
                }

                .fa-search {
                    margin-right: 0.5rem;
                }

                input {
                    border: none;
                    border-bottom: 1px solid $black-color;
                    padding: 0.5rem 0;
                    font-weight: bold;
                    outline: none;

                    &:hover,
                    &:active,
                    &:focus {
                        border-bottom: 1px solid $primary-color;
                    }
                }
            }
        }
    }

    @media screen and (max-width: 768px) {
        margin-top: 5%;
    }
}

